mapper.select:
		select r, product_detail, TO_CHAR(product_price, '999,999,999,999,999') product_price , product_balance, product_sellcount, product_category, product_brand, product_name, product_id, REGEXP_SUBSTR(product_image, '[^,]+',1,1) product_image, product_release, sales_persent, sales_date 
        from (select rownum r, p.* from product p)
		where r between (#{array[0]}-1)*#{array[1]}+1 and #{array[0]}*#{array[1]}

    select * from (
		select rownum r, product_detail, TO_CHAR(product_price, '999,999,999,999,999') product_price , product_balance, product_sellcount, product_category, product_brand, product_name, product_id, REGEXP_SUBSTR(product_image, '[^,]+',1,1) product_image, product_release, sales_persent, sales_date
		from (select * from product  

                         product_price desc 
                         product_price asc 
                          product_release DESC    
                          product_sellcount DESC                           

			)
		)
	where r between (#{curPage}-1)*#{pageListSize}+1 and #{curPage}*#{pageListSize}
